mapper.select:
SELECT userid, year, month, day, start_h, start_m, end_h, end_m, biko1, biko2, creation_time, modification_time
FROM workmonth
WHERE userid = #{userid}
AND year = #{year}
AND month = #{month}
ORDER BY userid ASC;

mapper.update:
UPDATE workmonth set start_h = #{start_h}, start_m = #{start_m}, end_h = #{end_h}, end_m = #{end_m}, biko1 = #{biko1}, biko2 = #{biko2}
WHERE userid = #{userid}
AND year = #{year}
AND month = #{month}
AND day = #{day}

mapper.delete:
DELETE
FROM workmonth
WHERE userid = #{userid}
AND year = #{year}
AND month = #{month}
